import React from "react";
import { setAlert } from "../actions/alerts";
import { connect } from "react-redux";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isloading: true };
  }

  async postPost(title, text) {
    console.log(title, text);
    await fetch("/api/posts", {
      method: "POST",
      headers: {
        "x-auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNWY4MmE2MDM0YWE1NjMwOWE4MzI5OGVjIn0sImlhdCI6MTYwMjU5MTM0MywiZXhwIjoxNjA2MTkxMzQzfQ.LR8krDpB70rSN60l8G3s3yrODGPogxeTu_mY4LWhrmw",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: title,
        text: text,
      }),
    });
  }

  onChangeTitle = (e) => {
    this.setState({ title: e.target.value });
    console.log(this.state);
  };
  onChangeText = (e) => {
    this.setState({ text: e.target.value });
    console.log(this.state);
  };
  onSubmit = (e) => {
    console.log(this.state);
    const { title, text } = this.state;
    if (title || text == null) {
      this.props.setAlert("AAAAAAAAAAAAAAA", "AAAAAAAAA");
    }
    this.postPost(this.state.title, this.state.text);
  };

  render() {
    return (
      <div>
        <form onSubmit={(e) => this.onSubmit(e)}>
          <div>
            <input type="text" onChange={this.onChangeTitle} />
          </div>
          <div>
            <input type="text" onChange={this.onChangeText} />
          </div>
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default connect(setAlert)(Form);
