import React from "react";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isloading: true };
  }

  async postPost(title, text) {
    await fetch("/api/posts", {
      method: "POST",
      headers: {
        "x-auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNWY4MmE2MDM0YWE1NjMwOWE4MzI5OGVjIn0sImlhdCI6MTYwMjU3NzQ4NiwiZXhwIjoxNjA2MTc3NDg2fQ.pLZw3DGPHhjDTnvQGWdIFHrV4n_Aa65hkcaLGZreK8I",
      },
    });
  }

  onChangeTitle = (e) => {
    this.setState({ title: e.target.value });
  };
  onChangeText = (e) => {
    this.setState({ text: e.target.value });
  };
  onSubmit = () => {
    this.postPost(this.state.title, this.state.text);
  };

  render() {
    return (
      <div>
        <form onSubmit={(e) => onSubmit(e)}>
          <input type="text" onChange={this.onChangeTtitle} />
          <input type="text" onChange={this.onChangeText} />
        </form>
      </div>
    );
  }
}

export default Form;
