import React from "react";

class Posts extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [], isloading: true };
  }
  async getPosts() {
    let posts = await fetch("/api/posts", {
      method: "GET",
      headers: {
        "x-auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNWY4MmE2MDM0YWE1NjMwOWE4MzI5OGVjIn0sImlhdCI6MTYwMjU3NzQ4NiwiZXhwIjoxNjA2MTc3NDg2fQ.pLZw3DGPHhjDTnvQGWdIFHrV4n_Aa65hkcaLGZreK8I",
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        return data;
      });
    this.setState({ posts, isloading: false });
  }

  componentDidMount = () => {
    console.log("AAAAAAAAAAAAA");
    this.getPosts();
  };
  render = () => {
    console.log("BBBBBBB");
    const { posts, isloading } = this.state;
    console.log(isloading, posts);
    if (isloading) {
      console.log("AAAAAAAAAAA");
      return <div></div>;
    } else {
      return posts.map((element) => {
        return (
          <div className="container">
            <div className="post_title" key={element._id}>
              {element.title}
            </div>
            <div className="post_text" key={element._id}>
              {element.text}
            </div>
          </div>
        );
      });
    }
  };
}

export default Posts;
