import { SET_ALERT, REMOVE_ALERT } from "../actions/types";
import uuid from "uuid";

export const setAlert = (msg, alertType) => (dispatch) => {
  const id = uuid.v4();

  const payload = {
    msg,
    alertType,
    id,
  };

  dispatch({
    type: SET_ALERT,
    payload,
  });

  setTimeout(() => dispatch({ type: REMOVE_ALERT, payload: id }), 5000);
};
