import React from "react";

class Posts extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [], isloading: true };
  }
  async getPosts() {
    let token = localStorage.getItem("token");

    let posts = await fetch("/api/posts", {
      method: "GET",
      headers: {
        "x-auth-token": token,
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        return data;
      });
    this.setState({ posts, isloading: false });
  }

  async onDelete(id) {
    console.log(id);
    let token = localStorage.getItem("token");
    await fetch("/api/posts", {
      method: "DELETE",
      headers: {
        "x-auth-token": token,
      },
      body: JSON.stringify({
        id,
      }),
    });
    let posts = this.getPosts();

    console.log("AAAAAAAAA", posts);
    this.setState(posts);
  }

  componentDidMount = () => {
    this.getPosts();
  };
  render = () => {
    const { posts, isloading } = this.state;
    console.log(isloading, posts);
    if (isloading) {
      return <div></div>;
    } else {
      return posts.map((element) => {
        return (
          <div className="post" key={element._id}>
            <div className="post_name">{element.name}</div>
            <div className="post_title">{element.title}</div>
            <div className="post_text">{element.text}</div>
            <button
              onClick={() => {
                this.onDelete(element._id);
              }}
            >
              delete
            </button>
          </div>
        );
      });
    }
  };
}

export default Posts;
